import { createContext } from "react";

const productsArr = [
  {
    id: "1",
    text: "Leica M6 (Body)",
    price: 1599.99,
    image:
      "https://www.lcegroup.co.uk/NewProductImages1/ADDBAD66-353B-4F91-9735-28A4BCEE8C56.jpg",
  },
  {
    id: "2",
    text: "Minolta",
    price: 699.99,
  },
  {
    id: "3",
    text: "Nikon",
    price: 599.99,
  },
];

function getProductData(id) {
  let productData = productsArr.find((product) => product.id === id);
  return productData;
}

// export const  createContext({
//     items: [],
//     getProductData: () => {}
// })

export { productsArr };
