import React, { useContext, useState } from "react";
import logo from "../assets/TechVault_transparent.png";
import { Navbar, Container, Button, Modal } from "react-bootstrap";
import { CartContext } from "../context/CartContext";
import CartItem from "./CartItem";

const NavbarComponent = ({ product }) => {
  const [showModal, setShowModal] = useState(false);
  const cartContext = useContext(CartContext);
  const productQuantity = cartContext.items.reduce(
    (acc, num) => acc + num.quantity,
    0
  );

  const handleCloseModal = () => {
    setShowModal(false);
  };
  const handleShowModal = () => {
    setShowModal(true);
  };
  return (
    <>
      <Navbar expand="sm">
        <Navbar.Brand href="/">
          <img src={logo} width={150} />
        </Navbar.Brand>
        <Navbar.Toggle />
        <Navbar.Collapse className="justify-content-end">
          <Button onClick={handleShowModal} className="px-3">
            Saved ({productQuantity} Items)
          </Button>
          <Button onClick={handleShowModal}>
            In Cart: ({productQuantity} Items)
          </Button>
        </Navbar.Collapse>
      </Navbar>
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Shopping Cart</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {productQuantity > 0 ? (
            <>
              <p>Items in cart:</p>
              {cartContext.items.map((item, index) => (
                <CartItem product={item} key={index} />
              ))}
              <h1>Total: Â£{cartContext.getTotalCost().toFixed(2)}</h1>
              <Button variant="success">Checkout</Button>
            </>
          ) : (
            <h3>There are no items in your cart.</h3>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default NavbarComponent;
