import { createContext } from "react";

const productsArr = [
  {
    id: "1",
    text: "Leica M6 (Body)",
    price: 4599.99,
    image:
      "https://www.lcegroup.co.uk/NewProductImages1/ADDBAD66-353B-4F91-9735-28A4BCEE8C56.jpg",
  },
  {
    id: "2",
    text: "Minolta X-700",
    price: 699.99,
    image:
      "https://s.turbifycdn.com/aah/yhst-2405092478071/minolta-x-700-35mm-camera-with-50mm-lens-student-camera-used-31.png",
  },
  {
    id: "3",
    text: "Nikon FM3A",
    price: 599.99,
    image: "https://photo.fleurey.com/uploads/5/0/8/9/50899857/1438866506.png",
  },
  {
    id: "4",
    text: "Yashica Electro 35 GSN",
    price: 235.99,
    image: "https://filmadvance.com/wp-content/uploads/2013/02/DSC_2409.jpg",
  },
  {
    id: "2",
    text: "Minolta X-700",
    price: 699.99,
    image:
      "https://s.turbifycdn.com/aah/yhst-2405092478071/minolta-x-700-35mm-camera-with-50mm-lens-student-camera-used-31.png",
  },
  {
    id: "3",
    text: "Nikon",
    price: 599.99,
  },
];

function getProductData(id) {
  let productData = productsArr.find((product) => product.id === id);
  return productData;
}

// export const  createContext({
