import { createContext } from "react";

const productsArr = [
  {
    id: "1",
    text: "Canon AE-1",
    price: 299.99,
    image:
      "https://cdn.assets.lomography.com/f0/1537ca53a6d7bbb99d637dde9994e5f03a9d68/1200x921x2.jpg?auth=6dc8bfe48bec9b02dc534cccd97aea03df34bdf1",
  },
  {
    id: "2",
    text: "Olympus OM-1",
    price: 399.99,
    image:
      "https://cdn.assets.lomography.com/f8/b5238f918817c6360f3c523710dd561251d29c/640x541x2.jpg?auth=0eeb10f14b5a3c121942423b837ad0939452054e",
  },
  {
    id: "3",
    text: "Nikon F2",
    price: 599.99,
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSCOysT8VsSo01s5n8rbnxy1FTLCcVZzNO7Ig&s",
  },
];

function getProductData(id) {
  let productData = productsArr.find((product) => product.id === id);
  return productData;
}

