import React from "react";

const CancelPayment = () => {
  const history = useHistory();

  const handleRetry = () => {
    // Replace '/retry' with the actual route to retry the payment
    history.push("/retry");
  };

  return (
    <div className="cancel-container">
      <h1 className="cancel-message">You have cancelled your payment.</h1>
      <button className="retry-button" onClick={handleRetry}>
        Retry Payment
      </button>
    </div>
  );
};

export default CancelPayment;
