import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import ProductCarousel from "../components/ProductCarousel";
import PromoBar from "../components/PromoBar";
import { productsArr } from "../context/productStore";

const Home = () => {
  //   const products = productsArr.map((product) => product.text);
  //   const randomProduct =
  //     products[Math.floor(Math.random() * productsArr.length)];
  //   console.log(randomProduct);
  const randomIndex = Math.floor(Math.random() * productsArr.length);
  console.log(productsArr[randomIndex]);
  const randomProduct = productsArr[randomIndex];
  return (
    <>
      <Container fluid>
        <PromoBar />
        <ProductCarousel />
        <h2 align="center">Check these out</h2>
        <ul>
          <Row xs={1} md={3} className="g-4">
            <Card>{randomProduct}</Card>
          </Row>
        </ul>
      </Container>
    </>
  );
};

export default Home;
