import { createContext } from "react";

const productsArr = [
  {
    id: "1",
    text: "Leica",
    price: 1599.99,
  },
  {
    id: "2",
    text: "Minolta",
    price: 699.99,
  },
  {
    id: "3",
    text: "Nikon",
    price: 599.99,
  },
];

function getProductData(id) {
  let productData = productsArr.find((product) => product.id === id);
  return productData;
}

// export const  createContext({
//     items: [],
//     getProductData: () => {}
// })

export { productsArr };
